import React, { Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';
import { Link } from 'react-router-dom';

export default class BorrarPerfil extends Component
{
  constructor()
  {
    super();
    this.state = {
      perfiles: [],
      perfilBorradoId: 0,
    }
  }
  componentDidMount = () => {
    axios.get(url + '/perfil/listarActivos').then((resp) => {
        this.setState({
            perfiles: resp.data,              
        });
    }).catch((error) => {
        console.log(error);
    }
    )
  };
  elegirPerfilBorrado = perfBorrado =>
  {
    this.setState({
        perfilBorradoId: perfBorrado.target.value
    })
    if (this.state.perfilBorradoId !== 0) {
      this.borrarBoton();
      
    }
    console.log(this.state.perfilBorradoId);
  }
  borrarBoton = () =>
  {
    axios.post(url + '/perfil/borrar?id=' + this.state.perfilBorradoId)
      .then(response =>
      {
        if (response.status === 200) {
          this.setState({ mensaje: "borrado correcto" });
          this.componentDidMount();
        }
       
      }).catch(error =>
      {
        console.log(error);
      })
  }
  
  render()
  {

    return (
      <div>
       
          <h1>Acciones Perfiles</h1>
         

            <ReactBoostrap.Table  hover variant="light-dark">
            <table className="table ">
            <thead>
              <th scope="col">ID</th >
              <th scope="col">Nombre</th>
              <th scope="col">Fecha</th>
              <th scope="col">Borrar</th>
              <th scope="col">Modificar</th>
              </thead>
              <tbody>
              {
                this.state.perfiles.map(elemento => (
                  <tr key={elemento.id} value={elemento} id={elemento.id}>
                    <td>{elemento.id}</td>
                    <td>{elemento.nombre}</td>
                    <td>{elemento.fecha}</td>
                    <td><ReactBoostrap.Button type="button" className="btn btn-secondary" value={elemento.id} id={elemento.id}
                      name={elemento.id} onClick={this.elegirPerfilBorrado}>Borrar</ReactBoostrap.Button></td>
                       <td>
                      <Link className='btn btn-primary' to={'/ModificarPerfil/' + elemento.id} >Modificar</Link>
                    </td>
                  </tr>
                ))
                }
                  </tbody>
              </table>
          </ReactBoostrap.Table>
          
        
      </div>
    )
  }
}
