import React, { Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';

export default class CrearEmpleado extends Component {

    constructor() {
        super();
        this.state = {
            mensaje: "",
            nombre: "",
            direccion: "",
            telefono: 0,
            mail: "",
            tipo: "",
            mensajeError: "",
            mensajeSuccess: "",
            errors: {}
        }
    }

    manejadorOnChange = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    clean() {
        this.setState({
            mensaje: "",
            mensajeSuccess: "",
            nombre: "",
            direccion: "",
            telefono: 0,
            mail: "",
            tipo: "",
            entryDate: "",
        });

    }

    handleValidation() {
        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;

        //Nombre
        if (this.state.nombre === "") {
            formIsValid = false;
            errors["nombre"] = "Debe ingresar el nombre!!!";
            document.getElementById("nombre").focus();
        }

        if (this.state.nombre !== "") {
            if (!(this.state.nombre).match(/^[a-zA-Z]+$/)) {
                formIsValid = false;
                errors["nombre"] = "El nombre solo acepta letras!!!";
                document.getElementById("nombre").focus();
            }
        }

        //Dirección
        if (this.state.direccion === "") {
            formIsValid = false;
            errors["direccion"] = "Debe ingresar la direccion!!!";
            document.getElementById("direccion").focus();
        }

        //Teléfono
        if (this.state.telefono === 0) {
            formIsValid = false;
            errors["telefono"] = "Debe ingresar el teléfono!!!";
            document.getElementById("telefono").focus();
        }

        //Mail
        if (this.state.mail === "") {
            formIsValid = false;
            errors["mail"] = "Debe ingresar el mail!!!";
            document.getElementById("mail").focus();
        }

        if (typeof this.state.mail !== "undefined") {
            let lastAtPos = this.state.mail.lastIndexOf('@');
            let lastDotPos = this.state.mail.lastIndexOf('.');

            if (!(lastAtPos < lastDotPos && lastAtPos > 0 && this.state.mail.indexOf('@@') == -1 && lastDotPos > 2 && (this.state.mail.length - lastDotPos) > 2)) {
                formIsValid = false;
                errors["mail"] = "El mail no es válido!!!";
                document.getElementById("mail").focus();
            }
        }

        //Tipo
        if (this.state.tipo === "") {
            formIsValid = false;
            errors["tipo"] = "Debe ingresar el tipo de empleado!!!";
            document.getElementById("tipo").focus();
        }

        this.setState({ errors: errors });
        return formIsValid;
    }

    manejadorBoton = () => {
        if (this.handleValidation()) {
            axios.post(url + '/empleado/insertar?nombre=' + this.state.nombre + '&direccion='
                + this.state.direccion + '&telefono=' + this.state.telefono
                + '&email=' + this.state.mail + '&tipo=' + this.state.tipo)
                .then(response => {
                    if (response.status === 200) {
                        this.setState({
                            mensaje: "Ingreso Correcto",
                            mensajeSuccess: "ok",
                            nombre: "",
                            direccion: "",
                            telefono: "",
                            mail: "",
                            tipo: "",
                        });
                        document.getElementById("nombre").value = "";

                        document.getElementById("direccion").value = "";

                        document.getElementById("telefono").value = "";

                        document.getElementById("mail").value = "";

                        document.getElementById("tipo").value = "";

                    } else {
                        this.setState({ mensaje: "Ingreso Incorrecto", mensajeError: "error" });
                    }

                }).catch(error => {
                    console.log(error);
                })

        }
    }

    render() {
        return (
            <div>
                <div>
                    <h1>Alta Empleado</h1>
                    <form onSubmit={this.submit}>
                        <span style={{ color: "red" }}>{this.state.errors["nombre"]}</span>
                        <br />
                        <input type="text" id="nombre" class="fadeIn second"
                            name="nombre" placeholder="Nombre del empleado" onChange={this.manejadorOnChange} />
                        <br />
                        <span style={{ color: "red" }}>{this.state.errors["direccion"]}</span>
                        <br />
                        <input type="text" id="direccion" class="fadeIn second"
                            name="direccion" placeholder="Dirección del empleado" onChange={this.manejadorOnChange} />
                        <br />
                        <span style={{ color: "red" }}>{this.state.errors["telefono"]}</span>
                        <br />
                        <input type="number" id="telefono" class="fadeIn second"
                            name="telefono" placeholder="Teléfono del empleado" onChange={this.manejadorOnChange} />
                        <br />
                        <span style={{ color: "red" }}>{this.state.errors["mail"]}</span>
                        <br />
                        <input type="text" id="mail" class="fadeIn second"
                            name="mail" placeholder="Mail del empleado" onChange={this.manejadorOnChange} />
                        <br />
                        <span style={{ color: "red" }}>{this.state.errors["tipo"]}</span>
                        <br />
                        <select name="tipo" id="tipo" className="form-control" onChange={this.manejadorOnChange} style={{ width: 170 }}>
                            <option value="ADMINISTRATIVO" >Administrativo</option>
                            <option value="MECANICO" >Mecanico</option>
                        </select>
                        <br /><br />
                        <ReactBoostrap.Button variant="success" onClick={this.manejadorBoton}
                            setInterval={"clean()", 5000}>Crear</ReactBoostrap.Button>
                    </form>
                    {this.state.mensajeSuccess === "ok" ?
                        <div class="alert alert-success" role="alert">
                            {this.state.mensaje}
                        </div> : (this.state.mensajeError === "error") ?
                            <div class="alert alert-danger" role="alert">
                                {this.state.mensaje}
                            </div> : null
                    }
                </div>
            </div>
        )

    }
}
