import React, { Component } from 'react';
import '../App.css';
import axios from 'axios';
import { url } from './API.js';
import { Redirect } from "react-router-dom";

export default class Login extends Component {
  constructor() {
    super();
    this.state = {
      form: {
        user: "",
        password: "",
      },
      usuario: {},
      logged: null,
      redirect: false,
      perfil: '',
    }
  }

  manejadorOnChange = async e => {
    await this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value
      }
    })
  }


  manejadorBoton = (e) => {
    e.preventDefault();
    axios.get(url + '/usuario/buscarPorEmail/?email='
      + this.state.form.user + '&password=' + this.state.form.password)
      .then(response => {
        if (response.status >= 200 && response.status <= 205) {
          this.setState({
            usuario: response.data,
            logged: true,
            perfil: response.data.perfil,
            redirect: true,
            mensaje: "ingreso correcto",
            mensajeSuccess: "ok"
          });
        } else {
          this.setState({ mensaje: "Ingreso Incorrecto", mensajeError: "error" });
        }

        //console.log(this.state.usuario)
        //console.log(this.state.perfil)      
      }).catch(error => {
        console.log(error);
      })
  }


  render() {
    if (this.state.redirect !== false) {
      localStorage.setItem("perfil", this.state.perfil.nombre)
      localStorage.setItem("usuario", this.state.usuario.id)
      return <Redirect to={window.location.href = '/PantallaPrincipal'} />
    } else
      return (
        <div class="wrapper fadeInDown">
          <div id="formContent">
            <div class="fadeIn first">
              <img src="images/car-clean-logo.png" id="icon" width="100px" alt="User Icon" />
            </div>
            <br></br>
            <form onSubmit={this.onSubmit}>
              <input type="text" id="login" name="user" placeholder="Email del usuario" onChange={this.manejadorOnChange} required />
              <input type="password" id="password" name="password" placeholder="Password" onChange={this.manejadorOnChange} required />
              <input type="submit" value="Log In" onClick={this.manejadorBoton} />
            </form>
            <div id="formFooter">
              <a class="btn btn primary" href="/RegistroUsuario">Registrarse</a>
            </div>
            {this.state.mensajeSuccess === "ok" ?
              <div class="alert alert-success" role="alert">
                {this.state.mensaje}
              </div> : (this.state.mensajeError === "error") ?
                <div class="alert alert-danger" role="alert">
                  {this.state.mensaje}
                </div> : null
            }
          </div>
        </div>
      );
  }

}
