import React, {  Component } from 'react'
import { connect } from 'react-redux'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';


export default class ModificarCategoria extends Component {
    constructor(props) {
        super(props);
        this.state = {                    
            categoria:'',           
            nombre:"",  
            descuento:"",       
            mensaje: "",
            mensajeError: "",
            mensajeSuccess: "",   
            errors: {}         
        }
    }
    componentDidMount = () => {
        console.log(this.props.match.params.id)
        axios.get(url + '/categoria/buscarPorId?id=' + this.props.match.params.id).then((resp) => {                    
            this.setState({                
                categoria:resp.data                              
            });
        }).catch((error) => {
            console.log(error);
        }
        )
    };

    manejadorOnChange = e =>
    {
        this.setState({[e.target.name]: e.target.value })
    }

    handleValidation(){
        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;

        //Nombre
        if (this.state.nombre === "") {
           formIsValid = false;
           errors["nombre"] = "Debe ingresar el nombre!!!";           
        }

        if (this.state.nombre !== ""){
            if(!(this.state.nombre).match(/^[a-zA-Z]+$/)){
               formIsValid = false;
               errors["nombre"] = "El nombre solo acepta letras!!!";
               document.getElementById("nombre").focus();
            }        
        }
 

        //Descuento
         if (this.state.descuento === "") {
            formIsValid = false;
            errors["descuento"] = "Debe ingresar el descuento!!!";            
        }

        this.setState({errors: errors});
       return formIsValid;
    }


    botonModificar = () =>
    {     
        if(this.handleValidation()){             
            axios.post(url + '/categoria/modificar/?id=' + this.props.match.params.id + '&nombre=' + this.state.nombre +
            '&descuento='+ this.state.descuento)
            .then(response =>
                {
                    if (response.status === 200) {
                        this.setState({ mensaje: "Ingreso Correcto",mensajeSuccess:"ok" });
                    } else {
                        this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
                    }
    
                }).catch(error =>
                {
                    console.log(error);
                })
        }
    }


    render() {
        const {categoria, nombre, descuento} = this.state
        return (
            <div>
                <h1>Modificar Categoria</h1>
                <form onSubmit={this.submit}>
                    <span style={{color: "red"}}>{this.state.errors["nombre"]}</span>
                    <br/>
                    <input type="text" name='nombre' value={nombre} placeholder={"Nombre: " + categoria.nombre} onChange={this.manejadorOnChange} />
                    <br/>
                    <span style={{color: "red"}}>{this.state.errors["descuento"]}</span>
                    <br/>
                    <br/><input type="number" name='descuento' value={descuento} placeholder={"Descuento: " + categoria.descuento} onChange={this.manejadorOnChange} />             
                    <br /><ReactBoostrap.Button variant="primary" onClick={this.botonModificar}>Modificar</ReactBoostrap.Button>
                </form>
                {this.state.mensajeSuccess==="ok"?
                        <div class="alert alert-success" role="alert">
                            { this.state.mensaje }
                            </div> : (this.state.mensajeError === "error")?
                            <div class="alert alert-danger" role="alert">
                            { this.state.mensaje }
                            </div>:null
                        }
            </div>
        )
    }
}
