import React, { Component } from 'react';
import * as ReactBoostrap from 'react-bootstrap';
import axios from 'axios'
import { connect } from 'react-redux'


export default class ListarCategoria extends Component {

  constructor() {
    super();
    this.state = {
        categorias: [],
        mensaje: "",           
        nombre:"",
        descuento:"",
        mensajeError: "",
        mensajeSuccess:"",
    }
  }

  componentDidMount = () => {
    axios.get('http://localhost:9090/categorias/listar/').then((resp) => {
      console.log(resp.data);
      this.setState({
        categorias: resp.data,
      });
    }).catch((error) => {
      console.log(error);
    }
    )
  };
  render() {
    return (
      <div>
        <h1 >Lista de Categorias</h1>
        <div>
          <ReactBoostrap.Table hover variant="light-dark">
            <table className="table ">
              <thead>
                <td scope="col">ID</td >
                <th scope="col">Nombre</th>
                <th scope="col">Descuento</th>               
              </thead>
              <tbody>
                {
                  this.state.categorias.map(elemento => (
                    <tr key={elemento.id} value={elemento} id={elemento.id}>
                      <td>{elemento.id}</td>
                      <td>{elemento.nombre}</td>
                      <td>{elemento.descuento}</td>                      
                    </tr>
                  ))
                }
              </tbody>
            </table>
          </ReactBoostrap.Table>
        </div>
      </div>
    );

  }
}
