import React, { componentDidMount, Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';

export default class CrearServicio extends Component {

    constructor() {
        super();
        this.state = {
            servicios: [],
            log: "",
            mensaje: "",
            servicioBorrado: {},
            servicioBorradoId: 0
        }
    }

    componentDidMount = () => {
        axios.get('http://localhost:9090/servicios/insertar').then((resp) => {
            console.log(resp.data);
            this.setState({
                servicios: resp.data,
            });
        }).catch((error) => {
            console.log(error);
        }
        )
    };

    render() {
        return (
            <div>
                <div>
                    <h1>Alta servicio</h1>
                    <form onSubmit={this.submit}>
                        <input type="text" id="crearServicio" class="fadeIn second" name="nombre" placeholder="Nombre del servicio" onChange={this.manejadorOnChange} />
                        <br/> <br/>
                        <ReactBoostrap.Button variant="success" onClick={this.manejadorBoton} >Crear</ReactBoostrap.Button>                        
                    </form>
                </div>
            </div>
        )

    }
}
