import React, { componentDidMount, Component } from 'react'
import { connect } from 'react-redux'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { url } from '../API.js';

export default class ModificarCliente extends Component {
  constructor() {
    super();
    this.state = {
      clientes: [],
      clienteBorradoId: 0,
      proveedorModificadoId: 0
    }
  }
  componentDidMount = () => {
    axios.get(url + '/cliente/listarActivos').then((resp) => {
      this.setState({
        clientes: resp.data,
      });
    }).catch((error) => {
      console.log(error);
    }
    )
  };
  elegirClienteBorrado = cliBorrado => {
    this.setState({
      clienteBorradoId: cliBorrado.target.value
    })
    if (this.state.clienteBorradoId !== 0) {
      this.borrarBoton();
    }
    console.log(this.state.clienteBorradoId);
  }
  borrarBoton = () => {
    axios.post(url + '/cliente/borrar?id=' + this.state.clienteBorradoId)
      .then(response => {
        if (response.status === 200) {
          this.setState({ mensaje: "borrado correcto" });
          this.componentDidMount();
        }

      }).catch(error => {
        console.log(error);
      })
  }

  enviarCliente = clienteEnviado => {
    console.log(clienteEnviado.target.value)
  }

  render() {
    return (
      <div>
        <h1>Acciones Clientes</h1>
        <ReactBoostrap.Table hover variant="light-dark">
          <table className="table ">
            <thead>
              <th scope="col">ID</th >
              <th scope="col">Nombre</th>
              <th scope="col">Borrar</th>
              <th scope="col">Modificar</th>
            </thead>
            <tbody>
              {
                this.state.clientes.map(elemento => (
                  <tr key={elemento.id} value={elemento} id={elemento.id}>
                    <td>{elemento.id}</td>
                    <td>{elemento.nombre}</td>
                    <td><ReactBoostrap.Button type="button" className="btn btn-secondary" value={elemento.id} id={elemento.id}
                      name={elemento.id} onClick={this.elegirClienteBorrado}>Borrar</ReactBoostrap.Button></td>
                    <td>
                      <Link className='btn btn-primary' to={'/ModificarCliente/' + elemento.id} >Modificar</Link>
                    </td>
                  </tr>
                ))
              }
            </tbody>
          </table>
        </ReactBoostrap.Table>


      </div>
    )
  }
}
