import React, {  Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';

export default class CrearEmpleado extends Component {

    constructor() {
        super();
        this.state = {
            mensaje: "",
            employeName: "",
            address:"",
            phone:0,
            mail:"",
            type:"",
            form:{
                employeName:"",
                address:"",
                phone:0,
                mail:"",
                type:"",
            },
            mensajeError: "",
            mensajeSuccess:"",
        }
    }

    manejadorOnChange = async e => {
        await this.setState({
          form: {
            ...this.state.form,
            [e.target.name]: e.target.value
          }
        })
        console.log(this.state.form);
      }
    onChangeName = e =>
    {
        this.setState({
            employeName: e.target.value,
        })
    }

    onChangeEntryDate= e =>
    {
        this.setState({
            entryDate: e.target.value,
        })
    }
    onChangeAddress= e =>
    {
        this.setState({
            address: e.target.value,
        })
    }
    onChangePhone= e =>
    {
        this.setState({
            phone: e.target.value,
        })
    }
    onChangeMail= e =>
    {
        this.setState({
            mail: e.target.value,
        })
    }
    onChangeType= e =>
    {
        this.setState({
            type: e.target.value,
        })
    }
    clean (){
        this.setState({ 
            mensaje: "",
            mensajeSuccess:"",
            type: "",
            mail: "",
            phone:"",
            address:"",
            employeName:"",
            entryDate:"",
         });
                 
    }
    manejadorBoton = () =>
    {
        // if (this.state.usuarioElegido !== "") {
            axios.post(url + '/empleado/insertar?nombre=' + this.state.employeName + '&direccion=' 
            + this.state.address + '&telefono=' + this.state.phone
             + '&email=' + this.state.mail + '&tipo=' + this.state.type)
                .then(response =>
                {
                    if (response.status === 200) {
                        this.setState({ mensaje: "Ingreso Correcto",mensajeSuccess:"ok" });
                    } else {
                        this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
                    }

                }).catch(error =>
                {
                    console.log(error);
                })
        // } else {
        //     this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
        // }
    }

    render() {
        return (
            <div>
                <div>
                    <h1>Alta Empleado</h1>
                    <form onSubmit={this.submit}>
                        <input type="text" id="crearEmpleado" class="fadeIn second" 
                        name="employeName" placeholder="Nombre del empleado" onChange={this.manejadorOnChange} />
                         <input type="text" id="crearServicio" class="fadeIn second"
                            name="address" placeholder="Dirección del empleado" onChange={this.manejadorOnChange} />
                        <input type="text" id="crearServicio" class="fadeIn second"
                            name="phone" placeholder="Telefono del empleado" onChange={this.manejadorOnChange} />
                      <input type="text" id="crearServicio" class="fadeIn second"
                            name="mail" placeholder="Mail del empleado" onChange={this.manejadorOnChange} />
                      <select  className="form-control" onChange={this.manejadorOnChange}>
                        <option value="ADMINISTRATIVO" >Administrativo</option>
                        <option value="MECANICO" >Mecanico</option>
                      </select>
                        <br/> <br/>
                        <ReactBoostrap.Button variant="success" onClick={this.manejadorBoton}
                        >Crear</ReactBoostrap.Button>                        
                    </form>
                    {this.state.mensajeSuccess==="ok"?
                        <div class="alert alert-success" role="alert">
                            { this.state.mensaje }
                            </div> : (this.state.mensajeError === "error")?
                            <div class="alert alert-danger" role="alert">
                            { this.state.mensaje }
                            </div>:null
                        }
                </div>
            </div>
        )

    }
}
