import React, { Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';
import {validarDigitoVerificador,soloNumeros,validarCedula} from '../Utils.js';
export default class CrearProveedor extends Component {

    constructor() {
        super();
        this.state = {
            mensaje: "",
            nombre: "",
            direccion: "",
            email: "",
            telefono: '',
            mensajeError: "",
            cedula: '',
            fields: {},
            mensajeSuccess: "",
            errors: {}
        }
    }
    manejadorOnChange = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    handleValidation() {
        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;

        //Nombre
        if (this.state.nombre === "") {
            formIsValid = false;
            errors["nombre"] = "Debe ingresar el nombre";
            document.getElementById("nombre").focus();
        }

        if (this.state.nombre === "") {
            if (!(this.state.nombre).match(/^[a-zA-Z]+$/)) {
                formIsValid = false;
                errors["nombre"] = "El nombre solo acepta letras";
            }
        }
        const regex = /^[0-9]*$/;
        const soloNumeros = regex.test(this.state.cedula);
        //C.I.

        if (!soloNumeros) {
            formIsValid = false;
            errors["cedula"] = "Debe ingresar solo números";
            document.getElementById("cedula").focus();

        } else if (this.state.cedula === "") {
                formIsValid = false;
                errors["cedula"] = "Debe ingresar la cédula de Identidad";
                document.getElementById("cedula").focus();
            }else {
                if(validarCedula(this.state.cedula)){
                    formIsValid = false;
                    errors["cedula"] = "La cédula de identidad no es válida";
                    document.getElementById("cedula").focus();
                }
                
            }




        //Dirección
        if (this.state.direccion === "") {
            formIsValid = false;
            errors["direccion"] = "Debe ingresar la dirección";
            document.getElementById("direccion").focus();
        }

        //Mail
        if (this.state.email === "") {
            formIsValid = false;
            errors["email"] = "Debe ingresar el mail";
            document.getElementById("email").focus();
        }

        if (typeof this.state.email !== "undefined") {
            let lastAtPos = this.state.email.lastIndexOf('@');
            let lastDotPos = this.state.email.lastIndexOf('.');

            if (!(lastAtPos < lastDotPos && lastAtPos > 0 && this.state.email.indexOf('@@') == -1 && lastDotPos > 2 && (this.state.email.length - lastDotPos) > 2)) {
                formIsValid = false;
                errors["email"] = "El mail no es válido";
                document.getElementById("email").focus();
            }
        }

        //Teléfono
        if (this.state.telefono === "") {
            formIsValid = false;
            errors["telefono"] = "Debe ingresar el teléfono";
            document.getElementById("telefono").focus();
        }

        this.setState({ errors: errors });
        return formIsValid;
    }
   
    manejadorBoton = () => {
        if (this.handleValidation()) {
            axios.post(url + '/proveedor/insertar?nombre=' + this.state.nombre + '&cedula=' + this.state.cedula +
                '&direccion=' + this.state.direccion + '&telefono=' + this.state.telefono + '&email=' + this.state.email)
                .then(response => {
                    if (response.status === 200) {
                        this.setState({
                            mensaje: "Ingreso Correcto",
                            mensajeSuccess: "ok",
                            nombre: "",
                            cedula: "",
                            direccion: "",
                            telefono: "",
                            mail: "",

                        });
                        document.getElementById("nombre").value = "";
                        document.getElementById("cedula").value = "";
                        document.getElementById("direccion").value = "";
                        document.getElementById("telefono").value = "";
                        document.getElementById("email").value = "";

                    }
                }).catch(error => {
                    this.setState({
                        mensaje: error.response.data.message,
                        mensajeError: "error"
                    });
                    console.log(error.response);
                    console.log(error.response.data.message);

                })
        }
    }

    render() {
        const { nombre, direccion, email, telefono } = this.state
        return (
            <div>
                <div>
                    <h1>Alta Proveedor</h1>
                    <form onSubmit={this.submit}>
                        <span style={{ color: "red" }}>{this.state.errors["nombre"]}</span>
                        <br />
                        <input type="text" id="nombre" class="fadeIn second" name="nombre" placeholder="Nombre del proveedor" value={nombre} onChange={this.manejadorOnChange} required />
                        <br />
                        <span style={{ color: "red" }}>{this.state.errors["cedula"]}</span>
                        <br />
                        <input type="text" id="cedula" class="fadeIn second"
                            name="cedula" placeholder="C.I. del proveedor" onChange={this.manejadorOnChange} />
                        <br />
                        <span style={{ color: "red" }}>{this.state.errors["direccion"]}</span>
                        <br />
                        <input type="text" id="direccion" class="fadeIn second" name="direccion" placeholder="Direccion del proveedor" value={direccion} onChange={this.manejadorOnChange} required />
                        <br />
                        <span style={{ color: "red" }}>{this.state.errors["email"]}</span>
                        <br />
                        <input type="text" id="email" class="fadeIn second" name="email" placeholder="Email del proveedor" value={email} onChange={this.manejadorOnChange} required />
                        <br />
                        <span style={{ color: "red" }}>{this.state.errors["telefono"]}</span>
                        <br />
                        <input type="text" id="telefono" class="fadeIn second" name="telefono" placeholder="Telefono del proveedor" value={telefono} onChange={this.manejadorOnChange} required />
                        <br /> <br />
                        <ReactBoostrap.Button variant="success" onClick={this.manejadorBoton} >Crear</ReactBoostrap.Button>
                    </form>
                    {this.state.mensajeSuccess === "ok" ?
                        <div class="alert alert-success" role="alert">
                            {this.state.mensaje}
                        </div> : (this.state.mensajeError === "error") ?
                            <div class="alert alert-danger" role="alert">
                                {this.state.mensaje}
                            </div> : null
                    }
                </div>
            </div>
        )

    }
}
