import React, { componentDidMount, Component } from 'react'
import { connect } from 'react-redux'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';


export default class ModificarProducto extends Component {
    constructor(props) {
        super(props);
        this.state = {
            empleado: '', 
            ProductoModificado: "",
            ProductoIdMod: 0,  
            nombre:'',         
            mensaje: "",
            mensajeError: "",
            mensajeSuccess: "",            
        }
    }
    componentDidMount = () => {
        console.log(this.props.match.params.id)
        axios.get(url + '/empleados/buscarPorId?id=' + this.props.match.params.id).then((resp) => {                     
            this.setState({
                empleado: resp.data, 
            });
        }).catch((error) => {
            console.log(error);
        }
        )
    };

    manejadorOnChange = e =>
    {
        this.setState({[e.target.name]: e.target.value })
    }

    botonModificar = () =>
    {     
        if (this.state.nombre !== "") {            
            axios.post(url + '/empleados/modificar/?id=' + this.props.match.params.id + '&nombre=' + this.state.nombre +
            '&precio='+ this.state.precio +'&stock=' + this.state.stock)
            .then(response =>
                {
                    if (response.status === 200) {
                        this.setState({ mensaje: "Ingreso Correcto",mensajeSuccess:"ok" });
                    } else {
                        this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
                    }
    
                }).catch(error =>
                {
                    console.log(error);
                })
        } else {
            this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
        }
    }


    render() {
        const {producto, proveedor, nombre, precio, stock} = this.state
        return (
            <div>
                <h1>Modificar Producto</h1>
                <form onSubmit={this.submit}>
                    <input type="text" name='nombre' value={nombre} placeholder={"Nombre: " + producto.nombre} onChange={this.manejadorOnChange} />
                    <input type="text"  placeholder={"Cod Proveedor: " + proveedor.id + ""}  readonly="readonly" />
                    <input type="text" name='precio' value={precio} placeholder={"Precio: " + producto.precio} onChange={this.manejadorOnChange} />
                    <input type="text" name='stock' value={stock} placeholder={"Stock: " + producto.stock} onChange={this.manejadorOnChange} />
                    <br /><ReactBoostrap.Button variant="primary" onClick={this.botonModificar}>Modificar</ReactBoostrap.Button>
                </form>
                {this.state.mensajeSuccess==="ok"?
                        <div class="alert alert-success" role="alert">
                            { this.state.mensaje }
                            </div> : (this.state.mensajeError === "error")?
                            <div class="alert alert-danger" role="alert">
                            { this.state.mensaje }
                            </div>:null
                        }
            </div>
        )
    }
}
