import React, { Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';


export default class ModificarServicio extends Component {
    constructor(props) {
        super(props);
        this.state = {                    
            servicio:'',           
            nombre:'',         
            mensaje: "",
            mensajeError: "",
            precio:"",
            garantia:'',
            garantias:[],
            horasTrabajo:"",           
            mensajeSuccess: "",
            errors: {}            
        }
    }
    componentDidMount = () => {
        console.log(this.props.match.params.id)
        axios.get(url + '/servicio/buscarPorId?id=' + this.props.match.params.id).then((resp) => { 
            console.log(resp.data)                              
            this.setState({                
              servicio:resp.data                              
            });
        }).catch((error) => {
            console.log(error);
        }
        )
        axios.get(url + '/garantia/listarActivos').then((resp) => {
            console.log(resp.data);
            this.setState({
                garantias: resp.data,
            });
        }).catch((error) => {
            console.log(error);
        }
        )
    };

    manejadorOnChange = e =>
    {
        this.setState({[e.target.name]: e.target.value })
    }

    handleValidation(){
        let fields = this.state.fields;
        let errors = {};
        let formIsValid = true;

        //Nombre
        if (this.state.nombre === "") {
           formIsValid = false;
           errors["nombre"] = "Debe ingresar el nombre!!!";
           //document.getElementById("nombre").focus();
        }

        if (this.state.nombre !== ""){
            if(!(this.state.nombre).match(/^[a-zA-Z]+$/)){
               formIsValid = false;
               errors["nombre"] = "El nombre solo acepta letras!!!";
              //document.getElementById("nombre").focus();
            }        
        }

         //Precio
         if (this.state.precio === "") {
            formIsValid = false;
            errors["precio"] = "Debe ingresar el precio!!!";
            //document.getElementById("precio").focus();
         }

         //Garantia
        if (this.state.garantia === "") {
            formIsValid = false;
            errors["garantia"] = "Debe ingresar la garantÃ­a!!!";
            //document.getElementById("garantia").focus();
        }
 
        //Horas de trabajo
        if (this.state.horasTrabajo === "") {
            formIsValid = false;
            errors["horasTrabajo"] = "Debe ingresar la cantidad de horas de trabajo!!!";
            //document.getElementById("horastrabajo").focus();
        }

        this.setState({errors: errors});
       return formIsValid;
    }

    elegirGarantia = event => {
        console.log(event.target.value)
        this.setState({
            garantia: event.target.value,
        });
    }

    botonModificar = () =>
    {     
        if(this.handleValidation()){           
            axios.post(url + '/servicio/modificar/?id=' + this.props.match.params.id + '&nombre=' + this.state.nombre +
            '&precio='+ this.state.precio + '&horasTrabajo='+ this.state.horasTrabajo + '&GarantiaId='+ this.state.garantia) 
            .then(response =>
                {
                    if (response.status === 200) {
                        this.setState({ mensaje: "Ingreso Correcto",mensajeSuccess:"ok" });
                    } else {
                        this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
                    }
    
                }).catch(error =>
                {
                    console.log(error);
                })
        }
    }


    render() {
        const {servicio, nombre,precio, horasTrabajo} = this.state
        return (
            <div>
                <h1>Modificar Servicios</h1>
                <form onSubmit={this.submit}>
                    <span style={{color: "red"}}>{this.state.errors["nombre"]}</span>
                    <br/>
                    <input type="text" name='nombre' value={nombre} placeholder={"Nombre: " + servicio.nombre} onChange={this.manejadorOnChange} />                    
                    <br/>
                    <span style={{color: "red"}}>{this.state.errors["precio"]}</span>
                    <br/>
                    <input type="number" name='precio' value={precio} placeholder={"Precio: " + servicio.precio} onChange={this.manejadorOnChange} />                    
                    <br />
                    <span style={{color: "red"}}>{this.state.errors["horasTrabajo"]}</span>
                    <br/>
                    <input type="number" name='horasTrabajo' value={horasTrabajo} placeholder={"Horas de Trabajo: " + servicio.horasTrabajo} onChange={this.manejadorOnChange} />                    
                    <br/><br/>
                    <label>Seleccione los meses de garantia</label><br/>
                    <select  onChange={this.elegirGarantia}>                       
                       {this.state.garantias.map(garantiaElegida => (
                            <option                                                        
                                value={garantiaElegida.id}                                
                            >{garantiaElegida.cantidadMeses}</option>
                        ))}
                    </select>
                       <br/>
                    <br/><ReactBoostrap.Button variant="primary" onClick={this.botonModificar}>Modificar</ReactBoostrap.Button>
                </form>
                {this.state.mensajeSuccess==="ok"?
                        <div class="alert alert-success" role="alert">
                            { this.state.mensaje }
                            </div> : (this.state.mensajeError === "error")?
                            <div class="alert alert-danger" role="alert">
                            { this.state.mensaje }
                            </div>:null
                        }
            </div>
        )
    }
}
