import React, { Component } from 'react';
import * as ReactBoostrap from 'react-bootstrap';
import axios from 'axios'
import { url } from '../API.js';

export default class AgendarServicio extends Component
{

    constructor()
    {
        super();
        this.state = {
            servicios: [],
            servicioElegido: "",
            mensaje: "",
            idCli: 1,//a modo de prueba el id de cliente
            idSer: 0,
            fechaElegida: "",
            mensaje: "",
            mensajeError: "",
            mensajeSuccess: "",
        }
    }
    agarrarDate = fecha =>
    {
        this.setState({
            fechaElegida: fecha.target.value
        })
    }
    manejadorBoton = () =>
    {
        axios.post(url + '/servicioAgendado/insertar?idSer=' + this.state.idSer + "&idCli="
            + this.state.idCli + "&fechaElegida=" + this.state.fechaElegida)
            .then(response =>
            {
                if (response.status === 200) {
                    this.setState({ mensaje: "Ingreso Correcto", mensajeSuccess: "ok" });
                } else {
                    this.setState({ mensaje: "Ingreso Incorrecto", mensajeError: "error" });
                }

            }).catch(error =>
            {
                console.log(error);
            })
    }
    componentDidMount = () =>
    {
        axios.get(url + '/servicios/listar').then((resp) =>
        {
            console.log(resp.data);
            this.setState({
                servicios: resp.data,
            });
        }).catch((error) =>
        {
            console.log(error);
        }
        )
    };
    handleChange = event =>
    {
      console.log(event.target.value)
      this.setState({
        idSer: event.target.value,
      });
    }
    render()
    {
        return (
            <div className="container">
                <label id="labeles"
                    className="lead" >Agende su servicio</label>
                <div className="">
                    <select
                        onChange={this.handleChange}
                        className="form-control"
                        placeholderText="Servicios">
                        {/* <option hidden selected id="seleccionarServicio">Servicio</option> */}
                        {this.state.servicios.map(servicioElegido => (
                            <option
                                key={servicioElegido}
                                name="servicio"
                                value={servicioElegido.id}
                                selectedValue={servicioElegido}
                            >{servicioElegido.nombre}</option>
                        ))}</select>
                    <form onSubmit={this.submit}>
                        <input type="date" id="agendarServicio" class="fadeIn second" name="fechaElegida" placeholder="elija fecha"
                            value={this.state.fechaElegida} onChange={this.agarrarDate} />
                        <ReactBoostrap.Button type="submit" variant="success" class="fadeIn fourth" value="Crear Servicio" 
                        onClick={this.manejadorBoton}>Crear</ReactBoostrap.Button >
                    </form>
                    {this.state.mensajeSuccess === "ok" ?
                        <div class="alert alert-success" role="alert">
                            {this.state.mensaje}
                        </div> : (this.state.mensajeError === "error") ?
                            <div class="alert alert-danger" role="alert">
                                {this.state.mensaje}
                            </div> : null
                    }
                </div>
            </div>
        );

    }
}
