import React, { Component } from 'react';
import * as ReactBoostrap from 'react-bootstrap';
import axios from "axios";


export default class ListarServicios extends Component {

  state = {
    data: []
  }
  componentDidMount() {
    this.peticionGet();
  }

  peticionGet = () => {
    axios.get('http://localhost:9090/servicios/listar').then(response => {
      this.setState({ data: response.data });
      console.log(response.data);
    }).catch(error => {
      console.log(error.message);
    })
  }


  render() {
    return (
      <div>
        <h1>ListarServicios</h1>
        <div>
          <ReactBoostrap.Table striped bordered hover variant="dark">
            {
              this.state.data.map(servicio => {
                return (
                  <tr>
                    <td>{servicio.id}</td>
                    <td>{servicio.nombre}</td>
                    <td>
                      <ReactBoostrap.Button variant="secondary"  onClick={() => { this.seleccionarServicio(servicio); this.modalInsertar() }}></ReactBoostrap.Button>
                      {"   "}
                      <ReactBoostrap.Button variant="secondary" onClick={() => { this.seleccionarServicio(servicio); this.setState({ modalEliminar: true }) }}></ReactBoostrap.Button>
                    </td>
                  </tr>
                )
              })
            }
          </ReactBoostrap.Table>
        </div>
      </div>
    );


    //<ReactBoostrap.Table striped bordered hover variant="dark">           
    //<td><ReactBoostrap.Button variant="secondary">Borrar</ReactBoostrap.Button></td> 
    //</ReactBoostrap.Table>
  }
}
