import React, { componentDidMount,Component } from 'react'
import { connect } from 'react-redux'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';

export default class BorrarProveedor extends Component
{
  constructor()
  {
    super();
    this.state = {
      proveedores: [],
      proveedorBorradoId: 0,
      proveedorModificadoId:0
    }
  }
  componentDidMount = () => {
    axios.get('http://localhost:9090/proveedores/listar').then((resp) => {
        this.setState({
            proveedores: resp.data,              
        });
    }).catch((error) => {
        console.log(error);
    }
    )
  };
  elegirProveedorBorrado = proveBorrado =>
  {
    this.setState({
        proveedorBorradoId: proveBorrado.target.value
    })
    if (this.state.proveedorBorradoId !== 0) {
      this.borrarBoton();
      
    }
    console.log(this.state.proveedorBorradoId);
  }
  borrarBoton = () =>
  {
    axios.post('http://localhost:9090/proveedores/borrar?id=' + this.state.proveedorBorradoId)
      .then(response =>
      {
        if (response.status === 200) {
          this.setState({ mensaje: "borrado correcto" });
          this.componentDidMount();
        }
       
      }).catch(error =>
      {
        console.log(error);
      })
  }

  enviarProveedor = proveedorEnviado =>
  {    
    console.log(proveedorEnviado.target.id)
    this.setState({
      proveedorModificadoId: proveedorEnviado.target.value
  })
  if (this.state.proveedorModificadoId !== 0) {
      <ModificarProveedor mensaje={proveedorModificadoId}/>
  }
  console.log(this.state.proveedorBorradoId);
  }
  
  render()
  {

    return (
      <div>
       
          <h1>Borrar Proveedores</h1>
         

            <ReactBoostrap.Table  hover variant="light-dark">
            <table className="table ">
            <thead>
              <th scope="col">ID</th ><th scope="col">Nombre</th>
                <th scope="col"></th>
              </thead>
              <tbody>
              {
                this.state.proveedores.map(elemento => (
                  <tr key={elemento.id} value={elemento} id={elemento.id}>
                    <td>{elemento.id}</td>
                    <td>{elemento.nombre}</td>
                    <td><ReactBoostrap.Button type="button" className="btn btn-secondary" value={elemento.id} id={elemento.id}
                      name={elemento.id} onClick={this.elegirProveedorBorrado}>Borrar</ReactBoostrap.Button></td>
                    <td><ReactBoostrap.Button type="button" className="btn btn-primary" value={elemento.id} id={elemento.id}
                      name={elemento.id} onClick={this.enviarProveedor}>Modificar</ReactBoostrap.Button></td>
                  </tr>                  
                ))
                }
                  </tbody>
              </table>
          </ReactBoostrap.Table>
          
        
      </div>
    )
  }
}


function ModificarProveedor(props) {
  return 
  <div>   
  <div>
      <h1>Modificar Proveedor</h1>
      <form onSubmit={this.submit}>
          <input type="text"  class="fadeIn second" name="nombre" placeholder="Nombre del proveedor" value={nombre} onChange={this.manejadorOnChange} required/>
          <input type="text"  class="fadeIn second" name="direccion" placeholder="Direccion del proveedor" value={direccion} onChange={this.manejadorOnChange} required/>
          <input type="text"  class="fadeIn second" name="email" placeholder="Email del proveedor" value={email} onChange={this.manejadorOnChange} required/>
          <input type="text"  class="fadeIn second" name="telefono" placeholder="Telefono del proveedor"  value={telefono} onChange={this.manejadorOnChange} required/>
          <br/> <br/>
          <ReactBoostrap.Button variant="primary"  >Modificar</ReactBoostrap.Button>                        
      </form>
      {this.state.mensajeSuccess==="ok"?
          <div class="alert alert-success" role="alert">
              { this.state.mensaje }
              </div> : (this.state.mensajeError === "error")?
              <div class="alert alert-danger" role="alert">
              { this.state.mensaje }
              </div>:null
          }
  </div>
</div>
}
