import React, { useState, useEffect, Component } from 'react';
import '../App.css';
import axios from 'axios';



export default class Login extends Component {
  constructor() {
    super();
    this.state = {
     form:{ nombre: "",
      password: "",
    }
    }
}
  manejadorOnChange = async e => {
    await this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value
      }
    })
    console.log(this.state.form);
  }


  manejadorBoton = () => {
    axios.post('http://localhost:9090/usuarios/buscar/?nombre='
      + this.state.form.nombre + '&password=' + this.state.form.password)
      .then(response => {
        if (response.status === 200) {
          this.props.dispatch({ type: "PANTALLA-INICIO", payload: true });
        }
        console.log(response);
      }).catch(error => {
        console.log(error);
      })
  }



  render() {
    return (
      <div class="wrapper fadeInDown">
        <div id="formContent">
          <div class="fadeIn first">
            <img src="https://i.ibb.co/Q6FHzPJ/CAR-CLEAN.jpg" id="icon" width="100px" alt="User Icon" />
          </div>
          <br></br>
          <form onSubmit={this.submit}>
            <input type="text" id="login" name="nombre" placeholder="nombre" onChange={this.manejadorOnChange} required/>
            <input type="password" id="password" name="password" placeholder="password" onChange={this.manejadorOnChange} required/>
            <input type="submit" value="Log In" onClick={this.manejadorBoton} />
          </form>
          <div id="formFooter">
            <a class="underlineHover" href="#">Forgot Password?</a>
          </div>
        </div>
      </div>
    );
  }

}
