import React,{Component} from 'react';
import * as ReactBoostrap from 'react-bootstrap';
import axios from 'axios'

import { url } from '../API.js';

export default class ListarServicios extends Component {

  constructor() {
    super();
    this.state = {
        servicios: [],
        searchTerm: ""
    }
}

manejadorOnChange = e =>{
  this.setState({
    searchTerm:e.target.value,
  })
}

componentDidMount = () => {
    axios.get(url +'/servicio/listarActivos').then((resp) => {
        console.log(resp.data);
        this.setState({
            servicios: resp.data,              
        });
    }).catch((error) => {
        console.log(error);
    }
    )
};

render() {
    return (
      <div className="App">
        <h1 >Listar Servicios</h1>
        <input type="text" 
          placeholder="Buscar por nombre" 
          onChange={this.manejadorOnChange}
        />
        <br></br><br></br>
        <ReactBoostrap.Table   className="table " hover variant="light-dark">
          <thead>           
            <th scope="col">Nombre</th>
            <th scope="col">Precio</th>
            <th scope="col">Horas de Trabajo</th>
            <th scope="col">Garant√≠a</th>
              </thead>
                <tbody>
                {
                    this.state.servicios.filter((elemento) => {
                      if (this.searchTerm == "") {
                        return elemento
                      } else if (elemento.nombre.toLowerCase().includes(this.state.searchTerm.toLowerCase())) {
                        return elemento
                      }
                    }).map(elemento => (
                        <tr key={elemento.id} value={elemento} id={elemento.id}>                          
                          <td>{elemento.nombre}</td>
                          <td>$ {elemento.precio}</td>
                          <td>{elemento.horasTrabajo}</td>
                          <td>{elemento.garantia.cantidadMeses} mes/es</td>
                        </tr>
                    ))
                  }
                </tbody>
          </ReactBoostrap.Table>
      </div>
    );

  }
}
