import React, { useState, useEffect, Component } from 'react';
import '../App.css';
import PantallaInicio from './PantallaInicio';


function primerLlamado() {

  fetch('http://localhost:9090/')
    .then(function (response) {
      this.setState({
        personas: Object.entries(response.data),
      });
    })
    .then(function (myJson) {
      console.log(myJson);
    }).catch(function (error) {
      console.log("error " + error)
    }
    )
}


export default class Login extends Component {

  constructor() {
    super();
    this.state = {
      login: ""
    }
  }


  login = () => {
    axios.post('http://localhost:9090/').then((resp) => {
      console.log(resp.data);
      this.setState({
        personas: resp.data,

      });
    }).catch((error) => {
      console.log(error);
    }
    )
  };

  manejadorOnChange = async e => {
    await this.setState({
      form: {
        ...this.state.form,
        [e.target.name]: e.target.value
      }
    })
    console.log(this.state.form);
  }

  manejadorBoton = () => {
    axios.post('http://localhost:9090/usuarios/buscar/?nombre='
      + this.state.form.nombre + '&password=' + this.state.form.password)
      .then(response => {
        if (response.status === 200) {
          this.props.dispatch
            ({ type: "PANTALLA-INICIO", payload: true })
        }
        console.log(response);
      }).catch(error => {
        console.log(error);
      })
  }

  submit(e) {
    e.preventDefault();
  }


  render() {
    return (
      <div>
        <div class="wrapper fadeInDown">
          <div id="formContent">
            <div class="fadeIn first">
              <img src="https://i.ibb.co/Q6FHzPJ/CAR-CLEAN.jpg" id="icon" width="100px" alt="User Icon" />
            </div>
            <br></br>
            <form onSubmit={this.submit}>
              <input type="text" id="login" class="fadeIn second" name="nombre" placeholder="nombre" onChange={this.manejadorOnChange} />
              <input type="password" id="password" class="fadeIn third" name="password" placeholder="password" onChange={this.manejadorOnChange} />
              <input type="submit" class="fadeIn fourth" value="Log In" onClick={this.manejadorBoton} />
            </form>
            <div id="formFooter">
              <a class="underlineHover" href="#">Forgot Password?</a>
            </div>
          </div>
        </div>

      </div>
    );
  }

}
