import React, { Component } from 'react';
import * as ReactBoostrap from 'react-bootstrap';
import axios from 'axios'
import { url } from '../API.js';
import Button from 'react-bootstrap/Button'
import ButtonGroup from 'react-bootstrap/ButtonGroup'



export default class ListarAgenda extends Component {

  constructor() {
    super();
    this.state = {
      serviciosAgendados: [],
      log: "",
      mensaje: "",
      date: new Date(),
      tareas: [],
      estaCliqueado: false,
      estaIniciado: false,
      notificacion: {},
      idServicio: "",
      description: "",
      idCli: 1,
      usuario:[],
      fechaVacia: "VACIA",
      btnIniciar: false,//verdadero esta deshabilitado y falso esta habilitado
      btnFinalizar:true,
    }
    this.verTareas.bind();

  }

  onChangeDescription = e => {
    console.log(e.target.value);
    this.setState({
      description: e.target.value,
    })
  }

  verTareas = (serId) => {
    console.log(serId);
    axios.post(url + '/tareaAgendada/listasTareasServicio/?id=' + serId).then((resp) => {
      console.log(resp.data);
      this.setState({
        tareas: resp.data,
        estaCliqueado: true,
        idServicio: serId,
      });
    }).catch((error) => {
      console.log(error);
    }
    )
  }
  iniciarAlServicio = e => {
    console.log(this.state.idServicio)
    axios.post(url + '/servicioAgendado/iniciarServicioAgendado/?id=' + this.state.idServicio).then((resp) => {
      console.log(resp.data);
      if (resp.status === 200) {
        this.componentDidMount();

      }
    }).catch((error) => {
      console.log(error);
    }
    )
  }
  finalizarAlServicio = e => {
    console.log(this.state.idServicio)
    axios.post(url + '/servicioAgendado/finalizarServicioAgendado/?id=' + this.state.idServicio).then((resp) => {
      console.log(resp.data);
      if (resp.status === 200) {
        this.componentDidMount();
        this.verTareas(this.state.idServicio);
        this.finalizarNotificacion();
      }
    }).catch((error) => {
      console.log(error);
    }
    )
  }
  iniciarTarea = (tarId) => {
    console.log(tarId);
    axios.post(url + '/tareaAgendada/iniciarTarea/?id=' + tarId).then((resp) => {
      console.log(resp);
      if (resp.status === 200) {
        this.setState({
          estaInciado: true,  //boolean esta iniciado
          btnIniciar:true,
          btnFinalizar:false,
        });
        this.verTareas(this.state.idServicio);
        this.iniciarAlServicio();
        this.iniciarNotificacion();
        //al iniciar la tarea cambiar el estado al servicio a que esta en proceso
      }
    }).catch((error) => {
      console.log(error);

    }
    )
  }
  finalizarTarea = (tarId) => {
    console.log(tarId);
    axios.post(url + '/tareaAgendada/finalizarTarea/?id=' + tarId).then((resp) => {
      console.log(resp.data);
      this.setState({
        estaInciado: false,
        btnFinalizar:true,
        //boolean esta finalizado
        //y respuesta con la notificacion creada
        //cambiar el estado en finalizado al servicioagendado
      });
      this.verTareas(this.state.idServicio);
    }).catch((error) => {
      console.log(error);
    }
    )
  }
  btnFinalizar(id){

    //retornar un booleano
    return 
  }
  iniciarNotificacion = () => {
    axios.post(url + '/notificacion/iniciarNotificacion/?idCli=' + this.state.idCli + "&idSerAge="
      + this.state.idServicio).then((resp) => {
        console.log(resp.data);
        this.setState({

        });
      }).catch((error) => {
        console.log(error);
      }
      )
  }

  finalizarNotificacion = () => {
    // const cors = require('cors');
    // const express = require('express');
    // let app = express();
    // app.use(cors());
    // app.options('*', cors()); 
    axios.post(url + '/notificacion/finalizarNotificacion/?idCli=' + this.state.idCli + "&idSerAge="
      + this.state.idServicio + "&descripcion=" + this.state.description).then((resp) => {
        console.log(resp.data);
        this.setState({

        });
      }).catch((error) => {
        console.log(error);
      }
      )
  }
  componentDidMount = () => {
    axios.get(url + '/servicioAgendado/listarActivos').then((resp) => {
      console.log(resp.data);
      this.setState({
        serviciosAgendados: resp.data,
      });
    }).catch((error) => {
      console.log(error);
    }
    )
    axios.get(url + '/usuario/buscarPorId' 
    + localStorage.getItem("usuario")).then((resp) => {
      console.log(resp.data);
      this.setState({
        usuario: resp.data,
      });
    }).catch((error) => {
      console.log(error);
    }
    )

  };



  render() {
    return (
      <div>
        <h1 >Lista Agenda</h1>
        <ReactBoostrap.Table className="table " hover variant="light-dark">
          <thead>
            <th scope="col">Id</th >
            <th scope="col">Estado</th>
            <th scope="col">Fecha Elegida</th>
            <th scope="col">Fecha Registro</th>
            <th scope="col">Fecha Inicio</th>
            <th scope="col">Fecha Fin</th>
            <th scope="col">Id Servicio</th>
            {/*  <th scope="col">Fecha Fin</th> */}
          </thead>
          <tbody>
            {
              this.state.serviciosAgendados.map(elemento => (
                <tr key={elemento.id} value={elemento} id={elemento.id}>
                  <td>{elemento.id}</td>
                  <td>{elemento.estado.nombre}</td>
                  <td>{elemento.fechaElegida.substring(0, [10])}</td>
                  <td>{elemento.fechaRegistro.substring(0, [10])}</td>
                  <td>{elemento.fechaInicio !== null ? elemento.fechaInicio.substring(0, [10]) : this.state.fechaVacia}</td>
                  <td>{elemento.fechaFin !== null ? elemento.fechaFin.substring(0, [10]) : this.state.fechaVacia}</td>
                  <td>{elemento.servicio.id}</td>
                  <td>
                    <ButtonGroup>
                      <Button className="button" onClick={this.verTareas.bind(this, elemento.id)}>Ver tareas</Button>
                    </ButtonGroup>
                  </td>
                </tr>

              ))
            }

          </tbody>
        </ReactBoostrap.Table>
        <ReactBoostrap.Table>
          <thead>
            <th scope="col">Id</th >
            <th scope="col">Tarea nombre</th>
            <th scope="col">Tarea descripcion</th>
            <th scope="col">Estado</th>
            <th scope="col">Fecha Inicio</th>
            <th scope="col">Fecha Fin</th>
          </thead>
          <tbody>
            {this.state.estaCliqueado === true ?
              this.state.tareas.map(e => (
                <tr key={e.id} value={e} id={e.id}>
                  <td>{e.id}</td>
                  <td>{e.tarea.nombre}</td>
                  <td>{e.tarea.descripcion}</td>
                  <td>{e.estado.nombre}</td>
                  <td>{e.fechaInicio !== null ? e.fechaInicio.substring(0, [10]) : this.state.fechaVacia}</td>
                  <td>{e.fechaFin !== null ? e.fechaFin.substring(0, [10]) : this.state.fechaVacia}</td>
                  <td>
                    <ButtonGroup>
                      <Button className="button" disabled={this.btnIniciar(e.id)===e.id} 
                      onClick={this.iniciarTarea.bind(this, e.id)}>Iniciar</Button>
                    </ButtonGroup>
                  </td>
                  <td>
                    <ButtonGroup>
                      <Button className="button" disabled={this.btnFinalizar(e.id)} 
                      onClick={this.finalizarTarea.bind(this, e.id)}>Finalizar</Button>
                    </ButtonGroup>
                  </td>
                </tr>
              )) : null
            }
            <input type="text"  name="description"
              placeholder="Ingrese una descripción" onChange={this.onChangeDescription} />
            {/* <ButtonGroup> */}
              <Button className="secondary" onClick={this.finalizarAlServicio}>Finalizar Servicio</Button>
            {/* </ButtonGroup> */}


          </tbody>
        </ReactBoostrap.Table>
      </div>
    );

  }
}
