import React, { Component } from 'react'

import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';

export default class ModificarCliente extends Component {
  constructor() {
    super();
    this.state = {
      cliente: '',
      nombre: '',
      cedula: '',
      direccion: '',
      categorias: [],
      errors: {},
      catid: 0,
      mensajeError: "",
      mensajeSuccess: "",
      mensaje: "",
    }
  }
  componentDidMount = () => {
    console.log(this.props.match.params.id)
    axios.get(url + '/cliente/buscarPorId?id=' + this.props.match.params.id).then((resp) => {
      this.setState({
        cliente: resp.data,
      });
    }).catch((error) => {
      console.log(error);
    }
    )
    axios.get(url + '/categoria/listarActivos').then((resp) => {
      console.log(resp.data);
      this.setState({
        categorias: resp.data,
      });
    }).catch((error) => {
      console.log(error);
    }
    )
  };
  manejadorOnChange = e => {
    this.setState({ [e.target.name]: e.target.value })
  }

  handleValidation() {

    let errors = {};
    let formIsValid = true;

    //Nombre
    if (this.state.nombre === "") {
      formIsValid = false;
      errors["nombre"] = "Debe ingresar el nombre";

    }

    if (this.state.nombre !== "") {
      if (!(this.state.nombre).match(/^[a-zA-Z]+$/)) {
        formIsValid = false;
        errors["nombre"] = "El nombre solo acepta letras";
        //document.getElementById("nombre").focus();
      }
    }

    //Cedula
    const regex = /^[0-9]*$/;
    const soloNumeros = regex.test(this.state.cedula);
    if (this.state.cedula === "") {
      formIsValid = false;
      errors["cedula"] = "Debe ingresar la Cédula de Identidad";
      document.getElementById("cedula").focus();
    } else {
      if (this.state.cedula < 0) {
        formIsValid = false;
        errors["cedula"] = "Debe ingresar números positivos";
        document.getElementById("cedula").focus();
      } else {
        if (!soloNumeros) {
          formIsValid = false;
          errors["cedula"] = "Debe ingresar solo números";
          document.getElementById("cedula").focus();
        }
      }
    }

    //Direccion
    if (this.state.direccion === "") {
      formIsValid = false;
      errors["direccion"] = "Debe ingresar la dirección";

    }

    //Categoria
    if (this.state.catid === "") {
      formIsValid = false;
      errors["categoria"] = "Debe ingresar la categoría";

    }
    this.setState({ errors: errors });
    return formIsValid;
  }
  botonModificar = () => {
   this.limpiar();
    if (this.handleValidation()) {
      axios.post(url + '/cliente/modificar?id=' + this.props.match.params.id + '&nombre=' + this.state.nombre +
        '&direccion=' + this.state.direccion + '&cedula=' + this.state.cedula + '&idCategoria=' + this.state.catid)
        .then(response => {
          if (response.status === 200) {
            this.setState({
              mensaje: "Ingreso Correcto",
              mensajeSuccess: "ok",
              nombre: "",
              cedula: "",
              direccion: "",
            });
            document.getElementById("nombre").value = "";
            document.getElementById("cedula").value = "";
            document.getElementById("direccion").value = "";
          } else {
            this.setState({ mensaje: "Ingreso Incorrecto", mensajeError: "error" });
          }
        }).catch(error => {
          console.log(error);
        })
    }
  }
  componentWillUnmount() {
    clearInterval(this.timer);
  }
  limpiar() {
    this.timer = setInterval(() => {
      this.setState({
        mensajeSuccess: "",
        mensajeError: "",
        errors: "",
      });
    }
      , 5000);
  }
  elegirCategoria = event => {
    console.log(event.target.value)
    this.setState({
      catid: event.target.value,
    });
  }

  render() {
    const { cliente, nombre, cedula, direccion } = this.state
    return (
      <div>
        <h1>Modificar Clientes</h1>
        <form onSubmit={this.submit}>
          <span style={{ color: "red" }}>{this.state.errors["nombre"]}</span>
          <br />
          <input type="text" name='nombre' value={nombre} placeholder={"Nombre: " + cliente.nombre} onChange={this.manejadorOnChange} />
          <br />
          <span style={{ color: "red" }}>{this.state.errors["cedula"]}</span>
          <br />
          <input type="text" name='cedula' value={cedula} placeholder={"Cédula: " + cliente.cedula} onChange={this.manejadorOnChange} />
          <br />
          <span style={{ color: "red" }}>{this.state.errors["direccion"]}</span>
          <br />
          <input type="text" name='direccion' value={direccion} placeholder={"Dirección: " + cliente.direccion} onChange={this.manejadorOnChange} />
          <br />
          <span style={{ color: "red" }}>{this.state.errors["categoria"]}</span>
          <br />
          <select className="form-control" onChange={this.elegirCategoria}>
            <option disable defaultValue>Elige una opcion</option>
            {this.state.categorias.map(categoriaElegida => (
              <option
                key={categoriaElegida.id}
                name="catid"
                id="catid"
                value={categoriaElegida.id}
              >{categoriaElegida.nombre}</option>
            ))}</select>
          <br /><br />
          <br /><ReactBoostrap.Button variant="primary" onClick={this.botonModificar}>Modificar</ReactBoostrap.Button>
        </form>
        {this.state.mensajeSuccess === "ok" ?
          <div class="alert alert-success" role="alert">
            {this.state.mensaje}
          </div> : (this.state.mensajeError === "error") ?
            <div class="alert alert-danger" role="alert">
              {this.state.mensaje}
            </div> : null
        }
      </div>
    )
  }
}


