import React, { Component } from 'react';
import axios from 'axios';
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';
import { Link } from 'react-router-dom'


export default class ModificarEstado extends Component {
  constructor(props) {
    super(props);
    this.state = {
      estado: '',
      nombre: "",
      mensaje: "",
      mensajeError: "",
      mensajeSuccess: "",
    }
  }

  componentDidMount = () => {
    console.log(this.props.match.params.id)
    axios.get('http://localhost:9090/estado/buscarPorId?id=' + this.props.match.params.id).then((resp) => {
      this.setState({
        estado: resp.data,
      });
    }).catch((error) => {
      console.log(error);
    }
    )
  };

  elegirEstadoModificado = EstadoModificado => {
    this.setState({
      EstadoIdModificado: EstadoModificado.target.value
    })
    if (this.state.EstadoIdModificado !== 0) {
      this.borrarBoton();

    }
    console.log(this.state.EstadoIdModificado);
  }


  handleValidation() {
    let fields = this.state.fields;
    let errors = {};
    let formIsValid = true;

    //Nombre
    if (this.state.nombre === "") {
      formIsValid = false;
      errors["nombre"] = "Debe ingresar el nombre!!!";
      //document.getElementById("nombre").focus();
   }

    if (this.state.nombre !== "") {
      if (!(this.state.nombre).match(/^[a-zA-Z]+$/)) {
        formIsValid = false;
        errors["nombre"] = "El nombre solo acepta letras!!!";        
      }
    }

    //Valor
    if (this.state.valor === "") {
      formIsValid = false;
      errors["nombre"] = "Debe ingresar el nombre!!!";     
    }

    this.setState({ errors: errors });
    return formIsValid;
  }


  borrarBoton = () => {
    if (this.handleValidation()) {
      axios.post(url + '/estado/modificar?id=' + this.state.EstadoIdModificado)
        .then(response => {
          if (response.status === 200) {
            this.setState({ mensaje: "estado modificado" });
            this.componentDidMount();
          }

        }).catch(error => {
          console.log(error);
        })
    }
  }

  render() {
    const { estado, nombre, valor } = this.state
    return (
      <div>
        <h1>Modificar Estado</h1>
        <form onSubmit={this.submit}>
          {<span style={{ color: "red" }}>{this.state.errors["nombre"]}</span>}
          <br />
          <input type="text" name='nombre' value={nombre} placeholder={"Nombre: " + estado.nombre} onChange={this.manejadorOnChange} />
          {/*<span style={{ color: "red" }}>{this.state.errors["valor"]}</span>*/}
          <br />
          <input type="text" name='valor' value={valor} placeholder={"valor: " + estado.valor} onChange={this.manejadorOnChange} />

          <br /><ReactBoostrap.Button variant="primary" onClick={this.botonModificar}>Modificar</ReactBoostrap.Button>
        </form>
        {this.state.mensajeSuccess === "ok" ?
          <div class="alert alert-success" role="alert">
            {this.state.mensaje}
          </div> : (this.state.mensajeError === "error") ?
            <div class="alert alert-danger" role="alert">
              {this.state.mensaje}
            </div> : null
        }
      </div>
    );
  }
}
