import React, { Component } from 'react'
import { connect } from 'react-redux'
import axios from 'axios'
import { url } from '../API.js';
export default class ModificarServicio extends Component
{

  constructor()
  {
    super();
    this.state = {
      servicios: [],
      log: "",
      unServicio: {},
      servicioElegido: "",
      mensaje: "",
      servicioBorrado: {},
      servicioBorradoId: 0,
      form: {
        nombre: "nombre",
        precio: 100,
      },
      idServicio :0,
    }
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event)
  {
    console.log(event.target.value)
    //console.log(Object.values(event.target.value))
    this.setState({
      // form: {
      //   nombre: event.target.value.nombre,
      //   precio: event.target.value.precio
      // },
      idServicio: event.target.value,
    });
    this.cargar()
  }

  cargar = c =>
  {
    this.llamadaApi();
    this.setState({
      form: {
        nombre: this.state.unServicio.nombre,
        precio: this.state.unServicio.precio,
      }
    })
  }

  llamadaApi()
  {
    axios.post(url + '/servicios/buscarServicio/?id=' + this.state.idServicio).then((resp) =>
    {
      console.log(resp.data);
      this.setState({
        unServicio: resp.data,
      });
    }).catch((error) =>
    {
      console.log(error);
    }
    )
  }
  elegirServicio = servicio =>
  {
    console.log(servicio.target.value);
    this.setState({
      form: {
        nombre: servicio.target.value.nombre,
        precio: servicio.target.value.precio,
      }
    })
  }

  componentDidMount = () =>
  {
    axios.get(url + '/servicios/listar').then((resp) =>
    {
      console.log(resp.data);
      this.setState({
        servicios: resp.data,
      });
    }).catch((error) =>
    {
      console.log(error);
    }
    )
  };

  render()
  {
    return (
      <div className="container">
        <label id="labeles"
          className="lead" >Selecciona el servicio</label>
        <div className="">
          <select
            onChange={this.handleChange}
            className="form-control"
            placeholderText="Servicios">
            {/* <option hidden selected id="seleccionarServicio">Servicio</option> */}
            {this.state.servicios.map(servicioElegido => (
              <option
                key={servicioElegido}
                name="servicio"
                value={servicioElegido.id}
                selectedValue={servicioElegido}
              >{servicioElegido.nombre}</option>
            ))}</select>
          <form onSubmit={this.submit}>
            <input type="text" id="crearServicio" class="fadeIn second" name="nombre" placeholder="nombre" value={this.state.form.nombre} onChange={this.manejadorOnChange} />
            <input type="text" id="crearServicio" class="fadeIn second" name="precio" placeholder="nombre" value={this.state.form.precio} onChange={this.manejadorOnChange} />
            <input type="submit" class="fadeIn fourth" value="Crear Servicio" onClick={this.manejadorBoton} />
          </form>



        </div>

      </div>
    )

  }

}
