import React, {  Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js';


export default class ModificarProducto extends Component {
    constructor(props) {
        super(props);
        this.state = {
            empleado: '', 
            ProductoModificado: "",
            ProductoIdMod: 0,  
            nombre:'',         
            mensaje: "",
            mensajeError: "",
            mensajeSuccess: "",            
        }
    }
    componentDidMount = () => {
        console.log(this.props.match.params.id)
        axios.get(url + '/empleado/buscarPorId?id=' + this.props.match.params.id).then((resp) => {        
            console.log(resp.data)            
            this.setState({
                empleado: resp.data, 
            });
        }).catch((error) => {
            console.log(error);
        }
        )
    };

    manejadorOnChange = e =>
    {
        this.setState({[e.target.name]: e.target.value })
    }

    botonModificar = () =>
    {     
        if (this.state.nombre !== "") {            
            axios.post(url + '/estado/modificar/?id=' + this.props.match.params.id + '&nombre=' + this.state.nombre +
            '&valor='+ this.state.valor )
            .then(response =>
                {
                    if (response.status === 200) {
                        this.setState({ mensaje: "Ingreso Correcto",mensajeSuccess:"ok" });
                    } else {
                        this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
                    }
    
                }).catch(error =>
                {
                    console.log(error);
                })
        } else {
            this.setState({ mensaje: "Ingreso Incorrecto",mensajeError:"error" });
        }
    }


    render() {
        const {estado, nombre, valor} = this.state
        return (
            <div>
                <h1>Modificar Empleado</h1>
                <form onSubmit={this.submit}>
                    <input type="text" name='nombre' value={nombre} placeholder={"Nombre: " + empleado.nombre} onChange={this.manejadorOnChange} />
                    <input type="text" name='fechaIngreso' value={fechaIngreso} placeholder={"Fecha de Ingreso: " + empleado.fechaIngreso} onChange={this.manejadorOnChange} />
                    <input type="text" name='direccion' value={direccion} placeholder={"Direccion: " + empleado.direccion} onChange={this.manejadorOnChange} />
                    <input type="text" name='telefono' value={telefono} placeholder={"Telefono: " + empleado.telefono} onChange={this.manejadorOnChange} />
                    <input type="text" name='email' value={email} placeholder={"Email: " + empleado.email} onChange={this.manejadorOnChange} />                    
                    <br /><ReactBoostrap.Button variant="primary" onClick={this.botonModificar}>Modificar</ReactBoostrap.Button>
                </form>
                {this.state.mensajeSuccess==="ok"?
                        <div class="alert alert-success" role="alert">
                            { this.state.mensaje }
                            </div> : (this.state.mensajeError === "error")?
                            <div class="alert alert-danger" role="alert">
                            { this.state.mensaje }
                            </div>:null
                        }
            </div>
        )
    }
}
