import React, { componentDidMount, Component } from 'react'
import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { Modal, ModalBody, ModalFooter, ModalHeader } from 'reactstrap';

export default class CrearServicio extends Component {   
state={
    data:[],
    modalInsertar: false,
    modalEliminar: false,
    form:{
      id:'',
      nombre:'',
      tipoModal:''
    }
  }
  componentDidMount() {
    this.peticionPost();
  }
  peticionPost=async()=>{
    delete this.state.form.id;
    await axios.post('http://localhost:9090/servicios/insertar?nombre=' + this.state.form.nombre).then(response=>{
      this.modalInsertar();
      this.peticionGet();
    }).catch(error=>{
      console.log(error.message);
    })
  }

  modalInsertar=()=>{
    this.setState({modalInsertar: !this.state.modalInsertar});
  }


    render() {
        return (
            <div>
                <div>
                    <h1>Alta servicio</h1>
                    <form onSubmit={this.submit}>
                        <input type="text" id="crearServicio" class="fadeIn second" name="nombre" placeholder="Nombre del servicio" onChange={this.manejadorOnChange} />
                        <br/> <br/>
                        <ReactBoostrap.Button variant="success" onClick={()=>{this.setState({form:null, tipoModal: 'insertar'}); this.modalInsertar()}} >Crear</ReactBoostrap.Button>                        
                    </form>
                </div>
            </div>
        )

    }
}
