import React, { Component } from 'react'

import axios from 'axios'
import * as ReactBoostrap from 'react-bootstrap';
import { url } from '../API.js'; 
import { Link } from 'react-router-dom'


export default class ModificarEstado extends Component
{
  constructor()
  {
    super();
    this.state = {
      estados: [],
      EstadoModificado: "",
      EstadoIdModificado: 0,
      mensaje:"",
      errors: {}
    }
  }
  
  componentDidMount = () => {
    axios.get(url + '/estado/listarActivos').then((resp) => {
        this.setState({
        estados: resp.data,              
        });
    }).catch((error) => {
        console.log(error);
    }
    )
  };
  
  elegirEstadoModificado = EstadoModificado =>
  {
    this.setState({EstadoIdModificado: EstadoModificado.target.value
    })
    if (this.state.EstadoIdModificado !== 0) {
      this.borrarBoton();
      
    }
    console.log(this.state.EstadoIdModificado);
  }

  handleValidation(){
    let fields = this.state.fields;
    let errors = {};
    let formIsValid = true;

    //Nombre
    if (this.state.nombre === "") {
       formIsValid = false;
       errors["nombre"] = "Debe ingresar el nombre!!!";
       document.getElementById("nombre").focus();
    }

    if (this.state.nombre !== ""){
        if(!(this.state.nombre).match(/^[a-zA-Z]+$/)){
           formIsValid = false;
           errors["nombre"] = "El nombre solo acepta letras!!!";
           document.getElementById("nombre").focus();
        }        
    }

    this.setState({errors: errors});
    return formIsValid;
  }


  borrarBoton = () =>
  {
    if(this.handleValidation()){           
      axios.post(url +'/estado/modificar?id=' + this.state.EstadoIdModificado)
        .then(response =>
        {
          if (response.status === 200) {
            this.setState({ mensaje: "estado modificado" });
            this.componentDidMount();
          }
        
        }).catch(error =>
        {
          console.log(error);
        })
      }
  }
  
  render()
  {
    return (
      <div>
          <h1>Modificar Estados</h1>
            <ReactBoostrap.Table  hover variant="light-dark">
            <table className="table">
                <thead>
                    <th scope="col">ID</th >
                    <th scope="col">Nombre</th>
                    <th scope="col"></th>
                </thead>
                <tbody>
                {
                    this.state.estados.map(elemento => (
                    <tr key={elemento.id} value={elemento} id={elemento.id}>
                        <td>{elemento.id}</td>
                        <td>{elemento.nombre}</td>
                        <td>
                            <Link className="btn btn-warning" to={"/EditarEstado/" + elemento.id}>Editar</Link>
                        </td>
                    </tr>
                    ))
                }
                </tbody>
            </table>
          </ReactBoostrap.Table>
          
        
      </div>
    )
  }
}
